import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';  // React Router for page navigation

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [msg, setMsg] = useState('');
  const history = useHistory();  // React Router hook for navigation

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',  // Send cookies with request
        body: JSON.stringify({ email, password }),
      });
      const data = await response.json();
      if (response.ok) {
        setMsg(data.message || 'Login successful!');
        history.push('/dashboard');  // Redirect to the dashboard
      } else {
        setMsg(data.error || 'Login failed');
      }
    } catch (error) {
      console.error(error);
      setMsg('Error while logging in.');
    }
  };

  return (
    <div>
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
      {msg && <div>{msg}</div>}
    </div>
  );
}

export default Login;
